  "ethosuximide_repeat": {
    "title": "Ethosuximide",
    "primaryType": "substance",
    "tags": [
      "Pharmacology",
      "Neurology",
      "Anticonvulsants",
      "USMLE"
    ],
    "classificationPath": [
      "Pharmacology",
