  "2b10e164": {
    "title": "Hepatotoxicity of Anticonvulsants",
    "primaryType": "finding",
    "tags": [
      "Pharmacology",
      "Toxicology",
      "Neurology",
      "Gastroenterology",
      "USMLE"
