  "8f0081d5": {
    "title": "Pes Cavus in Friedreich Ataxia",
    "primaryType": "finding",
    "tags": [
      "Neurology",
      "Genetics",
      "Orthopedics",
      "USMLE"
    ],
    "classificationPath": [
      "Neurology and Special Senses",
