  "911d3b47": {
    "title": "Oxytocin",
    "primaryType": "molecule",
    "tags": [
      "Endocrinology",
      "Obstetrics",
      "Neuroscience",
      "USMLE"
    ],
    "classificationPath": [
      "Endocrine",
      "Physiology",
      "Hypothalamic-Pituitary Axis"
