      },
      {
        "type": "differentiated_from",
        "to": "9c80344d"
      },
      {
        "type": "is_treated_by",
        "to": "prostaglandin_analogs"
      },
      {
        "type": "is_treated_by",
        "to": "beta_blockers"
      }
    ]
  }
}
