  "ff9dd909": {
    "title": "Moraxella catarrhalis",
    "primaryType": "microbe",
    "tags": [
      "Microbiology",
      "Infectious Disease",
      "Otology",
      "Pulmonology",
      "USMLE"
    ],
    "classificationPath": [
      "Neurology and Special Senses",
