  "2341f9c0": {
    "title": "Levetiracetam",
    "primaryType": "drug",
    "tags": [
      "Pharmacology",
      "Neurology",
      "Epilepsy",
      "USMLE"
    ],
    "classificationPath": [
      "Neurology and Special Senses",
